/** Helper functions **/

function decodeToString(payload) {
    return String.fromCharCode.apply(String, payload);
}

function decodeToJson(payload) {
    // covert payload to string.
    var str = decodeToString(payload);

    // parse string to JSON
    var data = JSON.parse(str);
    return data;
}

function addDeviceName(msg){
    var result;
    if(msg.labels.name !== undefined){
        result = msg.labels.name; 
    } else if(msg.labels.name === undefined){
        result = msg.event.targetName.substr(38,57);
    }
    return result;
}

function addMsgData(msg, newMsg){
    if(msg.event.data.networkStatus !== undefined){
        newMsg.attributes.msgType = msg.event.eventType;
        newMsg.attributes.signal = msg.event.data.networkStatus.signalStrength;
    } else if(msg.event.data.batteryStatus !== undefined){
        newMsg.attributes.msgType = msg.event.eventType;
        newMsg.attributes.battery = msg.event.data.batteryStatus.percentage;
    } else if(msg.event.data.objectPresent !== undefined && msg.event.data.objectPresent.state === "PRESENT"){
        newMsg.attributes.msgType = msg.event.data.objectPresent.state;
        addCounters(msg, newMsg);
    } else if(msg.event.data.objectPresent !== undefined && msg.event.data.objectPresent.state === "NOT_PRESENT") {
        newMsg.attributes.msgType = msg.event.data.objectPresent.state;
    } else if(msg.event.data.touch !== undefined){
        newMsg.attributes.msgType = msg.event.eventType;
        addCounters(msg, newMsg);
    }
    return newMsg;
}

function dateToLocalUnixTimestamp(timestamp){
    var dateWithoutMilliseconds = timestamp.substr(0, 19);
    var localUnixTimeStamp = +new Date(dateWithoutMilliseconds);
    return localUnixTimeStamp;
}

function dateToLocalDate(timestamp){
    var localUnixTimeStamp = dateToLocalUnixTimestamp(timestamp);
    var localDate = timeConverter(localUnixTimeStamp);
    return localDate;
}

