function timeConverter(UNIX_timestamp) {
  var a = new Date(UNIX_timestamp);
  var year = a.getFullYear();
  var month = (a.getMonth() + 1) < 10 ? '0' + (a.getMonth() + 1) : (a.getMonth() + 1);
  var date = a.getDate() < 10 ? '0' + a.getDate() : a.getDate();
  var hour = a.getHours() < 10 ? '0' + a.getHours() : a.getHours();
  var min = a.getMinutes() < 10 ? '0' + a.getMinutes() : a.getMinutes();
  var sec = a.getSeconds() < 10 ? '0' + a.getSeconds() : a.getSeconds();
//   var time = `${year}/${month}/${date} - ${hour}:${min}:${sec}`;
  var time = year + '/' + month + '/' + date + ' - ' + hour + ':' + min + ':' + sec;
  return time;
}

function addCounters(msg, newMsg){
    var localUnixTimeStamp = dateToLocalUnixTimestamp(msg.event.timestamp);
    var a = new Date(localUnixTimeStamp);
    var hour = a.getHours();
    newMsg.attributes.detectionTs = dateToLocalUnixTimestamp(msg.event.timestamp); 
    newMsg.attributes.total = 1;
    newMsg.attributes.maintenance = 1;
    newMsg.attributes.totalGroupe = 1;
    newMsg.attributes.totalPerMonth = 1;
    newMsg.attributes.nuit = 0;
    newMsg.attributes.matin = 0;
    newMsg.attributes.apresmidi = 0;
    newMsg.attributes.soir = 0;
    if(hour < 6){
        newMsg.attributes.nuit = 1;
    } else if(hour >= 6 && hour < 12){
        newMsg.attributes.matin = 1;
    } else if(hour >= 12 && hour < 18){
        newMsg.attributes.apresmidi = 1;
    } else if(hour >= 18){
        newMsg.attributes.soir = 1;
    }
    return msg;
}

function createPayload(msg){
    var newMsg = {
        "deviceName": addDeviceName(msg),
        "deviceType": "Diagbox",
        "attributes": {
            "deviceId": msg.event.targetName.substr(38,57),
            "indicator": "ON",
            "lastMessage": dateToLocalDate(msg.event.timestamp),
            "ts": dateToLocalUnixTimestamp(msg.event.timestamp)
        }
    };
    addMsgData(msg, newMsg);
    return newMsg;
}

/** Decoder **/

var data = decodeToJson(payload);

var result = createPayload(data);

return result;
