configuration.jsScript:
function nextNodes(metadata, msg) {
    var results = []; // tableau des noms des prochains noeuds
    if (msg.statut === "Désarmée") {
        results.push('Déclenchement');
    }
    if (msg.lowBattery === "true") {
        results.push('Batterie faible');
    }
    if (msg.signalValue === "Mauvais") {
        results.push('Signal faible');
    }
    if (msg.indicator === "OFF") {
        results.push('Capteur inactif');
    }
    return results;
}

return nextNodes(metadata, msg);

function nextNodes(metadata, msg) {
    var results = []; // tableau des noms des prochains noeuds

    if(typeof(msg.values) !== "undefined" && typeof(metadata.ss_customAlarm) !== "undefined") {
        if(metadata.ss_customAlarm.trim() === "" || parseInt(metadata.ss_customAlarm) === 0) {
            if (msg.values.compteurA === 1) {
                results.push('1ere prise');
            } else if (msg.values !== undefined && msg.values.compteurA === 11) {
                results.push('11eme prise');
            } else if (msg.values !== undefined && msg.values.compteurA === 21) {
                results.push('21eme prise');
            }
        } else if(msg.values.compteurA !== 0 && msg.values.compteurA % parseInt(metadata.ss_customAlarm) === 0) {
            results.push('Alarme personnalisée');
        }
    }
    if(typeof(msg.values) !== "undefined" && typeof(metadata.ss_seuilAlerteReservoir) !== "undefined") {
        if(msg.values.compteurReservoir >= parseInt(metadata.ss_seuilAlerteReservoir)) {
            results.push("Réservoir plein");
        }
    }
    if (msg.indicator === "OFF") {
        results.push('Capteur inactif');
    }
    if (msg.lowBattery === "true") {
        results.push('Batterie faible');
    }
    if (msg.signalValue === "Mauvais") {
        results.push('Signal faible');
    }
    return results;
}

return nextNodes(metadata, msg);

function nextNodes(metadata, msg) {
    var results = []; // tableau des noms des prochains noeuds
    if(metadata.deviceType === "Ekomille") {
        results.push("Ekomille");
    }
    if(metadata.deviceType === "Snapbox") {
        results.push("Snapbox");
    }
    if(metadata.deviceType === "RD Box") {
        results.push("RD Box");
    }
    return results;
}

return nextNodes(metadata, msg);

