let $injector = widgetContext.$scope.$injector;
let customDialog = $injector.get(widgetContext.servicesMap.get('customDialog'));
let entityService = $injector.get(widgetContext.servicesMap.get('entityService'));
let assetService = $injector.get(widgetContext.servicesMap.get('assetService'));
let deviceService = $injector.get(widgetContext.servicesMap.get('deviceService'));
let attributeService = $injector.get(widgetContext.servicesMap.get('attributeService'));
let roleService = $injector.get(widgetContext.servicesMap.get('roleService'));
let entityRelationService = $injector.get(widgetContext.servicesMap.get('entityRelationService'));
let entityGroupService = $injector.get(widgetContext.servicesMap.get('entityGroupService'));
let userService = $injector.get(widgetContext.servicesMap.get('userService'));

const currentUser = widgetContext.currentUser;
const currentOwnerInfo = getCurrentOwnerInfo();

// La classe Entity permettra de créer l'objet "t" utilisé dans les requêtes de recherche d'entités
class PageLink {
  
    constructor(pageSize, page, textSearch, sortOrder) {
      this.textSearch = textSearch;
      this.pageSize = pageSize;
      this.page = page;
      this.sortOrder = sortOrder;
    }
  
    nextPageLink() {
      return new PageLink(this.pageSize, this.page + 1, this.textSearch, this.sortOrder);
    }
  
    toQuery() {
      let query = `?pageSize=${this.pageSize}&page=${this.page}`;
      if (this.textSearch && this.textSearch.length) {
        query += `&textSearch=${this.textSearch}`;
      }
      if (this.sortOrder) {
        query += `&sortProperty=${this.sortOrder.property}&sortOrder=${this.sortOrder.direction}`;
      }
      return query;
    }
}

// ************************************* INITIALISATION *************************************

openEditEntityDialog();

function openEditEntityDialog() {
    customDialog.customDialog(htmlTemplate, EditEntityDialogController).subscribe();
}

// ************************************* LOGGED IN USER INFO *************************************

function getCurrentOwnerInfo(){
    let currentOwnerInfo = { "type": "CUSTOMER", "id": currentUser.customerId};
    if(currentUser.authority === "TENANT_ADMIN"){
        currentOwnerInfo = { "type": "TENANT", "id": currentUser.tenantId };
    }
    return currentOwnerInfo;
}

// ************************************* GET ENTITYGROUPS *************************************

function EditEntityDialogController(instance) {
    let vm = instance;

    vm.entityName = entityName;
    vm.entityType = entityId.entityType;
    vm.assetGroupId = "";
    vm.userGroupId = "";
    vm.relatedDevicesInfo = [];
    vm.relatedZonesInfo = [];
    vm.deletableUsersInfo = [];

    vm.editEntityFormGroup = vm.fb.group({
        entityName: ['', [vm.validators.required]],
        entityType: ['', [vm.validators.required]],
        relatedDevicesInfo: vm.fb.array([]),
        relatedZonesInfo: vm.fb.array([]),
        deletableUsersInfo: vm.fb.array([])
    });

    getEntityInfo();

    vm.cancel = function() {
        vm.dialogRef.close(null);
    };

    function getEntityInfo() {
        const t = new PageLink(10000, 0, null, null);
        const currentOwnerId = currentOwnerInfo.id;
        let tasks = [
            entityService.getEntity(entityId.entityType, entityId.id),
            attributeService.getEntityAttributes(entityId, "SERVER_SCOPE", ['assetGroupId']),
            attributeService.getEntityAttributes(entityId, "SERVER_SCOPE", ['userGroupId']),
            entityRelationService.findByFromAndType(entityId, "assetContains"),
            entityRelationService.findByFromAndType(entityId, "Contains"),
            entityRelationService.findByFromAndType(entityId, "Manages")
        ];
        if(currentOwnerInfo.type === "TENANT") {
            tasks.push(
                deviceService.getTenantDevices(t),
                assetService.getTenantAssets(t),
                userService.getUserUsers(t)
            );
        } else if(currentOwnerInfo.type === "CUSTOMER") {
            tasks.push(
                deviceService.getCustomerDevices(currentOwnerId, t),
                assetService.getCustomerAssets(currentOwnerId, t),
                userService.getCustomerUsers(currentOwnerId, t)
            );
        }
        widgetContext.rxjs.forkJoin(tasks)
            .subscribe((results) => {
                const assetInfo = results[0];
                const assetGroupId = results[1][0].value;
                const userGroupId = results[2][0].value;
                const relatedDevicesIds = results[3].map(deviceRelation => { return deviceRelation.to.id});
                const relatedZonesIds = results[4].map(zoneRelation => { return zoneRelation.to.id});
                const relatedUsersIds = results[5].map(userRelation => { return userRelation.to.id});
                const allDevicesList = results[6].data;
                const allAssetsList = results[7].data;
                const allUsersList = results[8].data;

                const relatedDevicesInfo = findRelatedEntitiesInfo(allDevicesList, relatedDevicesIds);
                const relatedZonesInfo = findRelatedEntitiesInfo(allAssetsList, relatedZonesIds);
                const relatedUsersInfo = findRelatedEntitiesInfo(allUsersList, relatedUsersIds);
                const stockInfo = allAssetsList.find(asset => asset.type === "Stock" && asset.ownerId.id === assetInfo.ownerId.id);
                findDevicesOldRelations(relatedDevicesInfo);
                findDeletableUsersInfo(relatedUsersInfo);

                vm.assetGroupId = assetGroupId;
                vm.userGroupId = userGroupId;
                vm.relatedDevicesInfo = relatedDevicesInfo;
                vm.relatedZonesInfo = relatedZonesInfo;
                vm.stockInfo = stockInfo;

                vm.editEntityFormGroup.patchValue({
                    entityName: vm.entityName,
                    entityType: vm.entityType,
                    relatedDevicesInfo: vm.relatedDevicesInfo,
                    relatedZonesInfo: vm.relatedZonesInfo,
                    deletableUsersInfo: vm.deletableUsersInfo
                }, {emitEvent: false});

            });
    }

    function findRelatedEntitiesInfo(entityList, relatedEntitiesIds) {
        let results = [];
        entityList.forEach(entity => {
            if(relatedEntitiesIds.includes(entity.id.id)) {
                results.push(entity);
            }
        });
        return results;
    }

    function findDevicesOldRelations(relatedDevicesInfo) {
        relatedDevicesInfo.forEach(relatedDevice => {
            entityRelationService.findInfoByTo(relatedDevice.id)
                .subscribe((results) => {
                    relatedDevice.relationsInfo = results;
                });
        });

    }

    function findDeletableUsersInfo(relatedUsersInfo) {
        relatedUsersInfo.forEach(relatedUser => {
            entityRelationService.findByToAndType(relatedUser.id, "Manages")
                .subscribe((relations) => {
                    if(relations.length === 1) {
                        vm.deletableUsersInfo.push(relatedUser);
                    }
                });
        });
    }


    // ************************************* MANAGE DEVICES RELATIONS *************************************

    function deleteDevicesOldRelations(relatedDevicesInfo) {
        let tasks = [];
        relatedDevicesInfo.forEach(relatedDevice => {
            if(relatedDevice.relationsInfo.length > 0) {
                relatedDevice.relationsInfo.forEach(relation => {
                    tasks.push(entityRelationService.deleteRelation(relation.from, relation.type, relation.to));
                });
            }
        });
        if (tasks.length > 0) {
            return widgetContext.rxjs.forkJoin(tasks);
        } else {
            return widgetContext.rxjs.of([]);
        }
    }

    function addDevicesNewRelations(relatedDevicesInfo) {
        let tasks = [];
        const stockInfo = vm.stockInfo;
        relatedDevicesInfo.forEach(relatedDevice => {
            const newRelationWithStock = {
                "from": {
                    "entityType": stockInfo.id.entityType,
                    "id": stockInfo.id.id
                },
                "to": {
                    "entityType": relatedDevice.id.entityType,
                    "id": relatedDevice.id.id
                },
                "type": "Contains",
                "typeGroup": "COMMON"
            };
            const newDeviceAttributes = [
                { key: "zoneName", value: stockInfo.name },
            ];
            tasks.push(
                entityRelationService.saveRelation(newRelationWithStock),
                attributeService.saveEntityAttributes(relatedDevice.id, "SERVER_SCOPE", newDeviceAttributes)
            );
        });
        if (tasks.length > 0) {
            return widgetContext.rxjs.forkJoin(tasks);
        } else {
            return widgetContext.rxjs.of([]);
        }
    }

    // ************************************* MANAGE DEVICES TELEMETRY AND ATTRIBUTES *************************************

    function createTelemetryReset(deviceType) {
        let telemetryReset = [];
        const now = new Date().valueOf();
        if (deviceType === "Diagbox") {
            telemetryReset = [
                {key: 'total', value: 0},
                {key: 'totalGroupe', value: 0},
                {key: 'maintenance', value: 0},
                {key: 'matin', value: 0},
                {key: 'apresmidi', value: 0},
                {key: 'soir', value: 0},
                {key: 'nuit', value: 0}
            ];
        } else if (deviceType === "Ekomille" || deviceType === "RD Box" || deviceType === "Snapbox" || deviceType === "Dry Contact") {
            telemetryReset = [
                {key: 'compteurA', value: 0},
                {key: 'compteurB', value: 0},
                {key: 'compteurC', value: 0},
                {key: 'compteurD', value: 0},
                {key: 'compteurReservoir', value: 0},
                {key: 'compteurGroupe', value: 0},
                {key: 'matin', value: 0},
                {key: 'apresmidi', value: 0},
                {key: 'soir', value: 0},
                {key: 'nuit', value: 0}
            ];
        } else if (deviceType === "Delta P") {
            telemetryReset = [
                {key: 'pressureValue', value: 0},
                {key: 'pressureUnit', value: "pa"},
                {key: 'voltageValue', value: 0},
                {key: 'voltageUnit', value: "mV"},
                {key: 'firstLowPressureTs', value: now},
                {key: 'firstZeroVoltageTs', value: now},
                {key: 'pressureBreakdown', value: false},
                {key: 'voltageBreakdown', value: false}
            ];
        } else if (deviceType === "Motion") {
            telemetryReset = [
                {key: 'total', value: 0},
                {key: 'totalGroupe', value: 0},
                {key: 'maintenance', value: 0},
                {key: 'matin', value: 0},
                {key: 'apresmidi', value: 0},
                {key: 'soir', value: 0},
                {key: 'nuit', value: 0}
            ];
        } else if (deviceType === "Kapflex") {
            telemetryReset = [
                {key: 'hydroCarbonHeight', value: 0},
                {key: 'temperature', value: 0},
            ];
        }
        return telemetryReset;
    }
    
    function resetDevicesTelemetry(relatedDevicesInfo) {
        let tasks = [];
        relatedDevicesInfo.forEach(relatedDevice => {
            const telemetryReset = createTelemetryReset(relatedDevice.type);
            tasks.push(attributeService.saveEntityTimeseries(relatedDevice.id, "telemetry", telemetryReset));
        });
        if (tasks.length > 0) {
            return widgetContext.rxjs.forkJoin(tasks);
        } else {
            return widgetContext.rxjs.of([]);
        }
    }

    function changeDevicesMode(relatedDevicesInfo) {
        let tasks = [];
        relatedDevicesInfo.forEach(relatedDevice => {
            const attributesArray = [{key: "mode", value: "MAINTENANCE"}];
            tasks.push(attributeService.saveEntityAttributes(relatedDevice.id, "SERVER_SCOPE", attributesArray));
        });
        if (tasks.length > 0) {
            return widgetContext.rxjs.forkJoin(tasks);
        } else {
            return widgetContext.rxjs.of([]);
        }
    }

    // ************************************* MANAGE DEVICES ALARMS *************************************

    function resetDevicesAlarms(relatedDevicesInfo) {
        let tasks = [];
        const accessToken = getAccessToken();
        const config = { headers: { 'X-Authorization': `Bearer ${accessToken}`} };
        relatedDevicesInfo.forEach(async function (relatedDevice) {
            const alarmList = await getDeviceAlarms(relatedDevice, config);
            alarmList.forEach(alarm => {
                const deleteAlarmURL = `https://thingsboard-dev.sarpnet.fr/api/alarm/${alarm.id.id}`;
                const deleteAlarmTask = new Promise(resolve => { axios.delete(deleteAlarmURL, config )});
                tasks.push(deleteAlarmTask);
            });
        });
        if (tasks.length > 0) {
            return widgetContext.rxjs.forkJoin(tasks);
        } else {
            return widgetContext.rxjs.of([]);
        }
    }

    function getAccessToken(){
        return localStorage.getItem('jwt_token');
    }
    
    function getDeviceAlarms(relatedDevice, config) {
        const getDeviceAlarmsURL = `https://thingsboard-dev.sarpnet.fr/api/alarm/${relatedDevice.id.entityType}/${relatedDevice.id.id}?pageSize=10000&page=0`;
        return new Promise(resolve => { resolve(axios.get(getDeviceAlarmsURL, config).then(result => { return result.data.data } )) });
    }

    // ************************************* MANAGE ASSETS  *************************************
    
    function deleteZones(relatedZonesInfo) {
        let tasks = [];
        relatedZonesInfo.forEach(relatedZone => {
            tasks.push(assetService.deleteAsset(relatedZone.id.id));
        });
        if (tasks.length > 0) {
            return widgetContext.rxjs.forkJoin(tasks);
        } else {
            return widgetContext.rxjs.of([]);
        }
    }

    function deleteAsset(assetId) {
        let tasks = [];
        tasks.push(assetService.deleteAsset(assetId.id));
        if (tasks.length > 0) {
            return widgetContext.rxjs.forkJoin(tasks);
        } else {
            return widgetContext.rxjs.of([]);
        }
    }

    // ************************************* MANAGE USERS  *************************************

    function deleteUsers(deletableUsersInfo) {
        let tasks = [];
        deletableUsersInfo.forEach(deletableUser => {
            tasks.push(userService.deleteUser(deletableUser.id.id));
        });
        if (tasks.length > 0) {
            return widgetContext.rxjs.forkJoin(tasks);
        } else {
            return widgetContext.rxjs.of([]);
        }
    }

    // ************************************* MANAGE ENTITY GROUPS  *************************************

    function deleteEntityGroup(entityGroupId) {
        let tasks = [entityGroupService.deleteEntityGroup(entityGroupId)];
        if (tasks.length > 0) {
            return widgetContext.rxjs.forkJoin(tasks);
        } else {
            return widgetContext.rxjs.of([]);
        }
    }

    // ************************************* END  *************************************

    function end(){
        vm.editEntityFormGroup.markAsPristine();
        widgetContext.updateAliases();
        vm.dialogRef.close(null);
        // setTimeout(function (){ location.reload() }, 1000); // bug de thingsboard : les datasources du dashboard ne se mettent à jour que si reload
    }

    // ************************************* SAVE *************************************

    vm.save = function() {
        vm.editEntityFormGroup.markAsPristine();
        const relatedDevicesInfo = vm.relatedDevicesInfo;
        const relatedZonesInfo = vm.relatedZonesInfo;
        const assetId = entityId;
        const deletableUsersInfo = vm.deletableUsersInfo;
        const assetGroupId = vm.assetGroupId;
        const userGroupId = vm.userGroupId;
        widgetContext.rxjs.forkJoin([
            deleteDevicesOldRelations(relatedDevicesInfo),
            resetDevicesTelemetry(relatedDevicesInfo),
            resetDevicesAlarms(relatedDevicesInfo),
            changeDevicesMode(relatedDevicesInfo),
            deleteUsers(deletableUsersInfo),
            deleteEntityGroup(assetGroupId),
        ]).subscribe((results) => {
            widgetContext.rxjs.forkJoin([
                deleteEntityGroup(userGroupId),
                addDevicesNewRelations(relatedDevicesInfo),
                deleteZones(relatedZonesInfo),
                // deleteAsset(assetId)
            ]).subscribe((results) => {
                widgetContext.rxjs.forkJoin([
                    deleteAsset(assetId)
                ]).subscribe((result) =>
                    end()
                );
            });
        });
    }

}

