// ************************************* LOGGED IN USER INFO *************************************

function getCurrentOwnerInfo(){
    let currentOwnerInfo = { "type": "CUSTOMER", "id": currentUser.customerId};
    if(currentUser.authority === "TENANT_ADMIN"){
        currentOwnerInfo = { "type": "TENANT", "id": currentUser.tenantId };
    }
