// ************************************* GET ENTITYGROUPS *************************************

function EditEntityDialogController(instance) {
    let vm = instance;

    vm.entityName = entityName;
    vm.entityType = entityId.entityType;
    vm.relatedZones = [];
    vm.assetList = [];
    vm.assetGroupInfo = {};
    vm.userGroupInfo = {};
    vm.attributes = {};
    vm.entity = {};
    vm.googleGeocodedAddress = {'street_number': '', 'route': '', 'locality': '', 'postal_code': '', 'adresse': '', 'address': ''};

    vm.editEntityFormGroup = vm.fb.group({
        entityName: ['', [vm.validators.required]],
        entityType: [null],
        assetAddress: ['', [vm.validators.required, vm.validators.pattern(/^[a-zA-ZÀ-ÿ0-9-.,' ]*$/)]],
        zones: [0, [vm.validators.pattern(/^([0-9]|[1-9][0-9]|100)$/)]],
        attributes: vm.fb.group({
            adresse: [null],
            assetAddress: [null],
            assetGroupId: [null],
            "code postal": [null],
            commentaires: [null],
            emailContact1: [null],
            emailContact2: [null],
            emailList: [null],
            fonctionContact1: [null],
            fonctionContact2: [null],
            latitude: [null],
            longitude: [null],
            nomContact1: [null],
            nomContact2: [null],
            nombreCaves: [null],
            nombreEtages: [null],
            prenomContact1: [null],
            prenomContact2: [null],
            telephoneContact1: [null],
            telephoneContact2: [null],
            userGroupId: [null],
            ville: [null],
            imageURL: [null]
        })
    });

    getEntityInfo();

    // ************************************************ CHECK FORM VALIDITY ************************************************

    function checkFormValidity() {
        let alerts = "Alerte - Le formulaire est invalide pour les raisons suivantes :";
        const newLine = "\r\n"; 
        let result = true;

        const addressInput = vm.editEntityFormGroup.get('assetAddress').value.trim();
        const assetName = vm.editEntityFormGroup.get('entityName').value;

        const assetNameValidity = checkAssetNameValidity(assetName);
        const assetValidity = checkEntityExistence(vm.assetList, assetName);
        const addressValidity = checkAddressValidity(addressInput);

        if(assetNameValidity === false) {
            alerts += newLine;
            alerts += '- Le nom du chantier est vide';
            result = false;
        }
        if(assetValidity === false) {
            alerts += newLine;
            alerts += '- Un chantier du même nom existe déjà dans cette agence';
            result = false;
        }
        if(addressValidity === false){
            alerts += newLine;
            alerts += '- L\'adresse renseignée est invalide';
            result = false;
        }
        if (result === false){
            alert(alerts);
        }
        return result;
    }

    function checkAssetNameValidity(assetName) {
        let result = false;
        if(assetName.trim().length !== 0){
            result = true;
        }
        return result;
    }

    function checkEntityExistence(entityList, testedEntityName) {
        let result = false;
        const testedEntityCheck = entityList.find(element => element.name.trim() === testedEntityName);
        if(testedEntityCheck === undefined) {
            result = true;
        } else if (testedEntityName === vm.entityName) {
            result = true;
        }
