    // ************************************* GET ASSET AND ZONES INFO *************************************

    function getEntityInfo() {
            widgetContext.rxjs.forkJoin([
                entityService.getEntity(entityId.entityType, entityId.id),
                attributeService.getEntityAttributes(entityId, 'SERVER_SCOPE'),
                entityRelationService.findByFromAndType(entityId, "Contains"),
                attributeService.getEntityAttributes(entityId, 'SERVER_SCOPE', ['assetGroupId']),
                attributeService.getEntityAttributes(entityId, 'SERVER_SCOPE', ['userGroupId']),
                entityGroupService.getEntityGroupAllByOwnerId(currentOwnerInfo.type, currentOwnerInfo.id, "ASSET")
            ]).subscribe((results) => {
                const entity = results[0];
                const entityAttributes = results[1];
                const entityRelations = results[2];
                const assetGroupId = results[3][0].value;
                const userGroupId = results[4][0].value;
                const assetGroupAllId = results[5].id.id;

                getEntityAttributes(entityAttributes);
                getRelatedZonesInfo(entityRelations);
                getRelatedAssetGroupInfo(assetGroupId);
                getRelatedUserGroupInfo(userGroupId);
                getAssetList(assetGroupAllId);

                vm.entity = entity;
                vm.editEntityFormGroup.patchValue({
                    entityName: vm.entity.name,
                    entityType: vm.entityType,
                    assetAddress: vm.attributes.assetAddress,
                    attributes: vm.attributes,
                }, {emitEvent: false});
                vm.googleGeocodedAddress.adresse = vm.attributes.adresse;
                vm.googleGeocodedAddress.address = vm.attributes.assetAddress;
                vm.googleGeocodedAddress.postal_code = vm.attributes["code postal"];
                
        });
            
    }

    function getEntityAttributes(attributes) {
        for (var i = 0; i < attributes.length; i++) {
            vm.attributes[attributes[i].key] = attributes[i].value;
        }
    }

    function getRelatedZonesInfo(relationsInfo) {
        relationsInfo.forEach(relation => {
            widgetContext.rxjs.forkJoin([
                entityService.getEntity(relation.to.entityType, relation.to.id),
                attributeService.getEntityAttributes(relation.to, 'SERVER_SCOPE')
            ]).subscribe((results) => {
                let relatedZone = results[0];
                const attributes = results[1];
                relatedZone.attributes = {};
                getRelatedZoneAttributes(attributes, relatedZone);
                vm.relatedZones.push(relatedZone);
            });
        });
    }

    function getRelatedZoneAttributes(attributes, relatedZone) {
        for (var i = 0; i < attributes.length; i++) {
            relatedZone.attributes[attributes[i].key] = attributes[i].value;
        }
    }

    function getRelatedAssetGroupInfo(assetGroupId) {
        entityGroupService.getEntityGroup(assetGroupId)
