                vm.assetGroupInfo = assetGroupInfo;
            });
    }

    function getRelatedUserGroupInfo(userGroupId) {
        entityGroupService.getEntityGroup(userGroupId)
            .subscribe((userGroupInfo) => {
                vm.userGroupInfo = userGroupInfo;
            });
    }

    function getAssetList(assetGroupAllId) {
        const t = new PageLink(10000, 0, null, null);
        entityGroupService.getEntityGroupEntities(assetGroupAllId, t)
