    // ************************************************* CHANGE EXISTING ENTITIES NAMES ************************************************* // 

    function changeEntitiesNames() {
        let tasks = [];
        const formValues = vm.editEntityFormGroup.value;
        const relatedZonesData = vm.relatedZones;
        const assetGroupInfo = vm.assetGroupInfo;
        const userGroupInfo = vm.userGroupInfo;
        if (vm.entity.name !== formValues.entityName) {
            vm.entity.name = formValues.entityName;
            assetGroupInfo.name = assetGroupInfo.name.slice(0, 10) + vm.editEntityFormGroup.get('entityName').value;
            userGroupInfo.name = userGroupInfo.name.slice(0, 8) + vm.editEntityFormGroup.get('entityName').value;
            tasks.push(
                assetService.saveAsset(vm.entity),
                entityGroupService.saveEntityGroup(assetGroupInfo),
                entityGroupService.saveEntityGroup(userGroupInfo)
            );
            relatedZonesData.forEach(zone => {
                const indexToSlice = zone.name.indexOf("~") + 2;
                zone.name = zone.name.slice(0, indexToSlice) + vm.editEntityFormGroup.get('entityName').value;
                tasks.push(assetService.saveAsset(zone));
            });
        }
        if (tasks.length > 0) {
            return widgetContext.rxjs.forkJoin(tasks);
        } else {
            return widgetContext.rxjs.of([]);
        }
    }

    // ************************************************** CHANGE EXISTING ASSET ATTRIBUTES ********************************************** //

    function editAssetAttributes() {
        let tasks = [];
        let attributesArray = [];
        const assetId = entityId;
        const attributes = vm.editEntityFormGroup.get('attributes').value;
        for (let key in attributes) {
            if (attributes[key] !== vm.attributes[key]) {
                attributesArray.push({key: key, value: attributes[key]});
            }
        }
        if (attributesArray.length > 0) {
            tasks.push(attributeService.saveEntityAttributes(assetId, "SERVER_SCOPE", attributesArray));
        } 
        if (tasks.length > 0) {
            return widgetContext.rxjs.forkJoin(tasks);
        } else {
            return widgetContext.rxjs.of([]);
        }
    }

    // ************************************************ CHANGE EXISTING ENTITIES ATTRIBUTES ******************************************** //

    function editExistingZonesAttributes() {
        let tasks = [];
        const assetAttributes = vm.editEntityFormGroup.get('attributes').value;
        const assetId = entityId.id;
        const newZonesAttributes = {
            'assetAddress': assetAttributes.assetAddress,
            'assetGroupId': assetAttributes.assetGroupId,
            'assetId': assetId,
            'emailList': assetAttributes.emailList,
            'latitude': assetAttributes.latitude,
            'longitude': assetAttributes.longitude,
            'userGroupId': assetAttributes.userGroupId
        };
        vm.relatedZones.forEach(relatedZone => {
            const attributes = relatedZone.attributes;
            let attributesArray = [];
            for (let key in attributes) {
                if(key !== "emailList" && key !=="imageURL" && attributes[key] !== newZonesAttributes[key]){
                    attributesArray.push({key: key, value: newZonesAttributes[key]});
                }
            }
            if (attributesArray.length > 0) {
                tasks.push(attributeService.saveEntityAttributes(relatedZone.id, "SERVER_SCOPE", attributesArray));
            }
        });
        if (tasks.length > 0) {
            return widgetContext.rxjs.forkJoin(tasks);
        } else {
            return widgetContext.rxjs.of([]);
        }
    }

