    vm.cancel = function() {
        vm.dialogRef.close(null);
    };

    vm.save = function() {
        vm.editEntityFormGroup.markAsPristine();
        const formValidity = checkFormValidity();
        const assetInfo = vm.entity;
        if(formValidity === true){
            Promise.resolve(getGeocodeResult())
                .then(() => {
                    widgetContext.rxjs.forkJoin([
                        createNewZones(),
                        changeEntitiesNames(),
                        editAssetAttributes(),
                        editExistingZonesAttributes()
                    ]).subscribe((results) => {
                        const zonesInfoList = results[0];
                        widgetContext.rxjs.forkJoin([
                            addNewZonesToAssetGroup(zonesInfoList),
                            addNewZonesAttributes(zonesInfoList),
                            addNewZonesRelations(zonesInfoList),
                            changeZonesOwner(zonesInfoList, assetInfo)
                        ]).subscribe((results) => { end() });
                    });
                })
                .catch(e => {
                    console.log(e);
                    alert("Une erreur est survenue. Veuillez vérifier l'adresse renseignée et réessayer.");
                    vm.editEntityFormGroup.markAsPristine();
                });
        };
    }
