let $injector = widgetContext.$scope.$injector;
let customDialog = $injector.get(widgetContext.servicesMap.get('customDialog'));
let attributeService = $injector.get(widgetContext.servicesMap.get('attributeService'));
let assetService = $injector.get(widgetContext.servicesMap.get('assetService'));
let deviceService = $injector.get(widgetContext.servicesMap.get('deviceService'));
let entityRelationService = $injector.get(widgetContext.servicesMap.get('entityRelationService'));
let entityGroupService = $injector.get(widgetContext.servicesMap.get('entityGroupService'));
let entityService = $injector.get(widgetContext.servicesMap.get('entityService'));
let customerService = $injector.get(widgetContext.servicesMap.get('customerService'));

const currentUser = widgetContext.currentUser;
let currentOwnerInfo = getCurrentOwnerInfo();

// La classe Entity permettra de créer l'objet "t" utilisé dans les requêtes de recherche d'entités
class PageLink {
  
    constructor(pageSize, page, textSearch, sortOrder) {
      this.textSearch = textSearch;
      this.pageSize = pageSize;
      this.page = page;
      this.sortOrder = sortOrder;
    }
  
    nextPageLink() {
      return new PageLink(this.pageSize, this.page + 1, this.textSearch, this.sortOrder);
    }
  
    toQuery() {
      let query = `?pageSize=${this.pageSize}&page=${this.page}`;
      if (this.textSearch && this.textSearch.length) {
        query += `&textSearch=${this.textSearch}`;
      }
      if (this.sortOrder) {
        query += `&sortProperty=${this.sortOrder.property}&sortOrder=${this.sortOrder.direction}`;
      }
      return query;
    }
}

// ************************************* INITIALISATION *************************************

openAddEntityDialog();

function openAddEntityDialog() {
    customDialog.customDialog(htmlTemplate, AddEntityDialogController).subscribe();
}

// ************************************* LOGGED IN USER INFO *************************************

function getCurrentOwnerInfo(){
    let currentOwnerInfo = { "type": "CUSTOMER", "id": currentUser.customerId};
    if(currentUser.authority === "TENANT_ADMIN"){
        currentOwnerInfo = { "type": "TENANT", "id": currentUser.tenantId };
    }
    return currentOwnerInfo;
}

// ************************************* FORMULAIRE *************************************

function AddEntityDialogController(instance) {
    let vm = instance;
    vm.selectedDevices = ['']; // devices sélectionnés pour être migrés
    vm.deviceGroupList = ['']; // types de devices
    vm.selectedAsset = ['']; // chantier sélectionné contenant la zone recherchée
    vm.filteredAssets = ['']; // liste des chantiers après avoir été filtrée
    vm.assetZoneList = ['']; // liste des zones du chantier sélectionné
    vm.assetList = ['']; // liste des chantiers complète 
    vm.stockInfo = ['']; // info du ou des stocks disponibles pour l'utilisateur
    vm.selectedZone = ['']; // zone sélectionnée
    vm.filteredZones = ['']; // liste des zones
    vm.assetAndStockList = ['']; // liste des chantiers et des stocks
    vm.zoneAndStockList = ['']; // liste des zones et des stocks
    vm.deviceFilterValue = "";
    vm.assetFilterValue = "";
    vm.zoneFilterValue = "";

    vm.moveDevice = vm.fb.group({
        selectedDevices: [[], [vm.validators.required]], // liste des devices sélectionnés pour être migrés
        deviceGroupList:  ['', [vm.validators.required]], // 
        selectedAsset: ['', [vm.validators.required]],
        filteredAssets: ['', [vm.validators.required]],
        assetList : [''],
        zoneList: [''],
        selectedZone: ['', [vm.validators.required]],
        filteredZones: [''],
        deviceFilterValue: [''],
        assetFilterValue: [''],
        zoneFilterValue: [''],
    });

    getEntityInfo();

    // ************************************* GET ENTITY INFO *************************************

    function getEntityInfo() {
        
        const t = new PageLink(10000, 0, null, null);

        let tasks = [deviceService.getDeviceTypes()];
        if(currentUser.authority === "TENANT_ADMIN") {
            tasks.push(
                deviceService.getTenantDevices(t),
                assetService.getTenantAssets(t)
            );
        } else if(currentUser.authority === "CUSTOMER_USER") {
            const customerId = currentUser.customerId;
            tasks.push(
                deviceService.getCustomerDevices(customerId, t),
                assetService.getCustomerAssets(customerId, t)
            );
        }

        widgetContext.rxjs.forkJoin(tasks).subscribe((results) => {

            const deviceGroupList = results[0];
            const devicesResults = results[1].data;
            console.log(devicesResults);
            addInfoToDeviceGroups(deviceGroupList);
            getDevices(deviceGroupList, devicesResults);
            sortDeviceList(deviceGroupList);
            vm.deviceGroupList = deviceGroupList;

            const assetResults = results[2].data;
            console.log(assetResults);
            const assetList = assetResults.filter(asset => asset.type === "Building");
            const zoneList = assetResults.filter(asset => asset.type === "Zone");
            const ownerStock = assetResults.filter(asset => asset.type === "Stock");

            const assetAndStockList = [...assetList, ...ownerStock];
            const zoneAndStockList = [...zoneList, ...ownerStock];

            getAssetZones(assetList, zoneList);
            getStock(assetList, ownerStock);
            sortAssetList(assetList);
            vm.assetList = assetList;
            vm.filteredAssets = assetList;
            vm.assetAndStockList = assetAndStockList;
            vm.zoneAndStockList = zoneAndStockList;

        });
    }

    function addInfoToDeviceGroups(deviceGroupList) {
        deviceGroupList.forEach(deviceGroup => {
            deviceGroup.devices = [];
            deviceGroup.filteredDevices = [];
        });
    }

    function getDevices(deviceGroups, deviceList) {
        deviceList.forEach(device => {
            let targetDeviceGroup = deviceGroups.find(deviceGroup => (deviceGroup.type === device.type));
            if(device.label) {
                targetDeviceGroup.devices.push(device);
                targetDeviceGroup.filteredDevices.push(device);
            }
        });
    }

    function sortDeviceList(deviceGroupList) {
        deviceGroupList.forEach(deviceGroup => {
            deviceGroup.devices.sort(
                function(deviceA, deviceB) {
                    const deviceLabelA = deviceA.label.toLowerCase();
                    const deviceLabelB = deviceB.label.toLowerCase();
                    if (deviceLabelA < deviceLabelB) {
                    return -1;
                    } else if (deviceLabelA > deviceLabelB) {
                    return 1;
                    } else if (deviceLabelA === deviceLabelB) {
                        return 0;
                    }
                });
        });
    }

    function getStock(assetList, ownerStockInfo) {
        let stock = {
            "name": "STOCK",
            "type": "Stock",
            "id": {"id": null, "entityType": null},
            "zones": []
        };
        ownerStockInfo.forEach(ownerStock => {
            ownerStock.devicesToDelete = [];
            ownerStock.devicesToAdd = [];
            stock.zones.push(ownerStock);
        });
        assetList.push(stock);
    }

    function getAssetZones(assetList, zoneList) {
        assetList.forEach(asset => {
            asset.zones = [];
            entityRelationService.findByFromAndType(asset.id, "Contains")
                .subscribe(results => {
                    if(results.length > 0) {
                        let zonesInAsset = [];
                        results.forEach(result => {
                            const zoneInAsset = zoneList.find(zone => zone.id.id === result.to.id);
                            if (typeof(zoneInAsset) !== "undefined") {
                                zoneInAsset.devicesToDelete = [];
                                zoneInAsset.devicesToAdd = [];
                                zonesInAsset.push(zoneInAsset);
                            }
                        });
                        sortAssetList(zonesInAsset);
                        asset.zones = zonesInAsset;
                    }
                });
        });
    }

    function sortAssetList(assetList) {
        assetList.sort((a, b) => a.name.toLowerCase().localeCompare(b.name.toLowerCase()));
    }
  
    // ************************************* MANAGE DEVICES TELEMETRY AND ATTRIBUTES *************************************

    function createTelemetryReset(deviceType) {
        let telemetryReset = [];
        const now = new Date().valueOf();
        if (deviceType === "Diagbox") {
            telemetryReset = [
                {key: 'total', value: 0},
                {key: 'totalGroupe', value: 0},
                {key: 'maintenance', value: 0},
                {key: 'matin', value: 0},
                {key: 'apresmidi', value: 0},
                {key: 'soir', value: 0},
                {key: 'nuit', value: 0}
            ];
        } else if (deviceType === "Ekomille" || deviceType === "RD Box" || deviceType === "Snapbox" || deviceType === "Dry Contact") {
            telemetryReset = [
                {key: 'compteurA', value: 0},
                {key: 'compteurB', value: 0},
                {key: 'compteurC', value: 0},
                {key: 'compteurD', value: 0},
                {key: 'compteurReservoir', value: 0},
                {key: 'compteurGroupe', value: 0},
                {key: 'matin', value: 0},
                {key: 'apresmidi', value: 0},
                {key: 'soir', value: 0},
                {key: 'nuit', value: 0}
            ];
        } else if (deviceType === "Delta P") {
            telemetryReset = [
                {key: 'pressureValue', value: 0},
                {key: 'pressureUnit', value: "pa"},
                {key: 'voltageValue', value: 0},
                {key: 'voltageUnit', value: "mV"},
                {key: 'firstLowPressureTs', value: now},
                {key: 'firstZeroVoltageTs', value: now},
                {key: 'pressureBreakdown', value: false},
                {key: 'voltageBreakdown', value: false}
            ];
        } else if (deviceType === "Motion") {
            telemetryReset = [
                {key: 'total', value: 0},
                {key: 'totalGroupe', value: 0},
                {key: 'maintenance', value: 0},
                {key: 'matin', value: 0},
                {key: 'apresmidi', value: 0},
                {key: 'soir', value: 0},
                {key: 'nuit', value: 0}
            ];
        } else if (deviceType === "Kapflex") {
            telemetryReset = [
                {key: 'hydroCarbonHeight', value: 0},
                {key: 'temperature', value: 0},
            ];
        }
        return telemetryReset;
    }

    // ************************************* MANAGE DEVICES ALARMS *************************************

    function getAccessToken(){
        return localStorage.getItem('jwt_token');
    }
    
    function getDeviceAlarms(selectedDevice, config) {
        const getDeviceAlarmsURL = `https://thingsboard-dev.sarpnet.fr/api/alarm/${selectedDevice.id.entityType}/${selectedDevice.id.id}?pageSize=10000&page=0`;
        return new Promise(resolve => { resolve(axios.get(getDeviceAlarmsURL, config).then(result => { return result.data.data } )) });
    }


    

    // ************************************* FERMER *************************************

    vm.cancel = function() {
        vm.dialogRef.close(null);
    };
    
    vm.deviceFilter = function(value) {
        let filter = value.toLowerCase();
        vm.deviceGroupList.forEach(deviceGroup => {
            deviceGroup.filteredDevices = deviceGroup.devices.filter(device => device.label.toLowerCase().startsWith(filter));
        });
        vm.moveDevice.patchValue({
            deviceGroupList: vm.deviceGroupList
        });
    }

    vm.assetFilter = function(value) {
        vm.filteredAssets = searchAsset(value);
        vm.moveDevice.patchValue({
            filteredAssets: vm.filteredAssets
        });
    }
    
    function searchAsset(value) {
        let filter = value.toLowerCase();
        return vm.assetList.filter(option =>
          option.name.toLowerCase().startsWith(filter)
        );
    }

    vm.zoneFilter = function(value) {
        vm.filteredZones = searchZone(value);
        vm.moveDevice.patchValue({
            filteredZones: vm.filteredZones
        });
    }
    
    function searchZone(value) {
        let filter = value.toLowerCase();
        return vm.assetZoneList.filter(option =>
          option.name.toLowerCase().startsWith(filter)
        );
    }

    vm.onSelectedAsset = function(selectedAsset) {
        vm.selectedAsset = selectedAsset;
        vm.filteredZones = selectedAsset.zones;
        vm.assetZoneList = selectedAsset.zones;
        vm.moveDevice.patchValue({
            filteredZones: vm.filteredZones
        });
    }

    vm.onOpenedChange = function(event, filter) {
        switch (filter) {
            case 'deviceFilter':
                vm.deviceFilterValue = "";
                vm.deviceFilter(vm.deviceFilterValue);
                vm.moveDevice.patchValue({
                    deviceFilterValue: vm.deviceFilterValue
                });
                break;
            case 'assetFilter':
                vm.assetFilterValue = "";
                vm.assetFilter(vm.assetFilterValue);
                vm.moveDevice.patchValue({
                    assetFilterValue: vm.assetFilterValue
                });
                break;
            case 'zoneFilter':
                vm.zoneFilterValue = "";
                vm.zoneFilter(vm.zoneFilterValue);
                vm.moveDevice.patchValue({
                    zoneFilterValue: vm.zoneFilterValue
                });
                break;
            default:
                break;
        }
    }


    // ************************************* ACTIONS ************************************* 


    // ************************************* SUBMIT *************************************

    vm.save = function() {
        vm.moveDevice.markAsPristine();
        let selectedDevices = vm.moveDevice.get('selectedDevices').value;
        let selectedAsset = vm.moveDevice.get('selectedAsset').value;
        let selectedZone = vm.moveDevice.get('selectedZone').value;
        selectedDevices.forEach(device => {
            entityRelationService.findInfoByTo(device.id)
                .subscribe(results => {
                    const relations = results;
                    const assetContainsRelation = results.find(relation => relation.type === "assetContains");
                    const containsRelation = results.find(relation => relation.type === "Contains");
                    const currentAsset = typeof(assetContainsRelation) !== "undefined" ? vm.assetAndStockList.find(asset => asset.id.id === assetContainsRelation.from.id) : undefined;
                    const currentZone = typeof(containsRelation) !== "undefined" ? vm.zoneAndStockList.find(zone => zone.id.id === containsRelation.from.id) : undefined;
                    checkDeviceAssetChange(device, selectedAsset, currentAsset);
                    checkDeviceZoneChange(device, selectedZone, currentZone);
                    checkDeviceOwnerChange(device, selectedZone);
                    checkDeviceDestination(device, selectedZone);
                    
                    widgetContext.rxjs.forkJoin([
                        deleteDeviceOldRelations(relations),
                        createDeviceNewRelations(device, selectedAsset, selectedZone),
                        changeDeviceMode(device, selectedZone),
                        changeDeviceZoneAttributes(device, selectedZone),
                        resetDeviceTelemetry(device),
                        resetDeviceAlarms(device),
                        changeDeviceOwner(device, selectedZone)
                    ]).subscribe();
                });
        });
        widgetContext.updateAliases();
        vm.dialogRef.close(null);
    }

    /// NEW VERSION

    class Relation {
        constructor(fromEntity, toEntity, type) {
            this.from = {
                "entityType": fromEntity.id.entityType,
                "id": fromEntity.id.id
            };
            this.to = {
            "entityType": toEntity.id.entityType,
            "id": toEntity.id.id
            };
            this.type = type;
            this.typeGroup = "COMMON";
        }
    }

    function checkDeviceAssetChange(device, selectedAsset, currentAsset) {
        if(typeof(currentAsset) === "undefined") {
            device.changeAsset = true;
        } else if(selectedAsset.id.id !== currentAsset.id.id) {
            device.changeAsset = true;
        } else {
            device.changeAsset = false;
        }
    }

    function checkDeviceZoneChange(device, selectedZone, currentZone) {
        if(typeof(currentZone) === "undefined") {
            device.changeZone = true;
        } else if(selectedZone.id.id !== currentZone.id.id) {
            device.changeZone = true;
            currentZone.isDevicesToDelete = true;
            currentZone.devicesToDelete.push(device.id.id);
            selectedZone.isDevicesToAdd = true;
            selectedZone.devicesToAdd.push(device.id.id);
        } else {
            device.changeZone = false;
        }
    }

    function checkDeviceOwnerChange(device, selectedZone) {
        if(device.ownerId.id !== selectedZone.ownerId.id) {
            device.changeOwner = true;
        } else {
            device.changeOwner = false;
        }
    }

    function checkDeviceDestination(device, selectedZone) {
        if(selectedZone.type === "Stock") {
            device.moveToStock = true;
        } else {
            device.moveToStock = false;
        }
    }

    function deleteDeviceOldRelations(relations) {
        let tasks = [];
        relations.forEach(relation => {
            tasks.push(entityRelationService.deleteRelation(relation.from, relation.type, relation.to));
        });
        if (tasks.length > 0) {
            return widgetContext.rxjs.forkJoin(tasks);
        } else {
            return widgetContext.rxjs.of([]);
        }
    }

    function createDeviceNewRelations(device, selectedAsset, selectedZone) {
        let tasks = [];
        if(device.changeAsset === true || device.changeZone === true) {
            if(selectedZone.type === "Stock") {
                const newRelationWithStock = new Relation(selectedZone, device, "assetContains");
                tasks.push(
                    entityRelationService.saveRelation(newRelationWithStock)
                );
            } else {
                const newRelationWithAsset = new Relation(selectedAsset, device, "assetContains");
                const newRelationWithZone = new Relation(selectedZone, device, "Contains");
                tasks.push(
                    entityRelationService.saveRelation(newRelationWithAsset),
                    entityRelationService.saveRelation(newRelationWithZone)
                );
            }
        }
        if (tasks.length > 0) {
            return widgetContext.rxjs.forkJoin(tasks);
        } else {
            return widgetContext.rxjs.of([]);
        }
    }

    function changeDeviceMode(device, selectedZone) {
        let tasks = [];
        if(device.changeAsset === true || device.changeZone === true) {
            let modeAttribute = [];
            if(selectedZone.type === "Stock") {
                modeAttribute = [{key: "mode", value: "MAINTENANCE"}];
            } else {
                modeAttribute = [{key: "mode", value: "PROD"}];
            }
            tasks.push(attributeService.saveEntityAttributes(device.id, "SERVER_SCOPE", modeAttribute));
        }
        if (tasks.length > 0) {
            return widgetContext.rxjs.forkJoin(tasks);
        } else {
            return widgetContext.rxjs.of([]);
        }
    }

    function changeDeviceZoneAttributes(device, selectedZone) {
        let tasks = [];
        if(device.changeZone === true) {
            const zoneInfoAttributes = [
                { "key": "zoneName", "value": selectedZone.name },
                { "key": "zoneId", "value": selectedZone.id.id }
            ];
            tasks.push(attributeService.saveEntityAttributes(device.id, "SERVER_SCOPE", zoneInfoAttributes));
        }
        if (tasks.length > 0) {
            return widgetContext.rxjs.forkJoin(tasks);
        } else {
            return widgetContext.rxjs.of([]);
        }
    }

    function resetDeviceTelemetry(device) {
        let tasks = [];
        if(device.changeAsset === true) {
            const telemetryReset = createTelemetryReset(device.type);
            tasks.push(attributeService.saveEntityTimeseries(device.id, "telemetry", telemetryReset));
        }
        if (tasks.length > 0) {
            return widgetContext.rxjs.forkJoin(tasks);
        } else {
            return widgetContext.rxjs.of([]);
        }
    }

    async function resetDeviceAlarms(device) {
        let tasks = [];
        const accessToken = getAccessToken();
        const config = { headers: { 'X-Authorization': `Bearer ${accessToken}`} };
        if(device.changeAsset === true) {
            const alarmList = await getDeviceAlarms(device, config);
            alarmList.forEach(alarm => {
                const deleteAlarmURL = `https://thingsboard-dev.sarpnet.fr/api/alarm/${alarm.id.id}`;
                const deleteAlarmTask = new Promise(resolve => { axios.delete(deleteAlarmURL, config )});
                tasks.push(deleteAlarmTask);
            });
        }
        if (tasks.length > 0) {
            return widgetContext.rxjs.forkJoin(tasks);
        } else {
            return widgetContext.rxjs.of([]);
        }
    }

    function changeDeviceOwner(device, selectedZone) {
        let tasks = [];
        const newOwnerInfo = selectedZone.ownerId;
        if(device.changeOwner === true) {
            tasks.push(entityGroupService.changeEntityOwner(newOwnerInfo, device.id));
        }
        if (tasks.length > 0) {
            return widgetContext.rxjs.forkJoin(tasks);
        } else {
            return widgetContext.rxjs.of([]);
        }
    }

