let $injector = widgetContext.$scope.$injector;
let customDialog = $injector.get(widgetContext.servicesMap.get('customDialog'));
let assetService = $injector.get(widgetContext.servicesMap.get('assetService'));
let deviceService = $injector.get(widgetContext.servicesMap.get('deviceService'));
let attributeService = $injector.get(widgetContext.servicesMap.get('attributeService'));
let entityRelationService = $injector.get(widgetContext.servicesMap.get('entityRelationService'));
let userService = $injector.get(widgetContext.servicesMap.get('userService'));
let dashboardService = $injector.get(widgetContext.servicesMap.get('dashboardService'));
let entityGroupService = $injector.get(widgetContext.servicesMap.get('entityGroupService'));
let entityService = $injector.get(widgetContext.servicesMap.get('entityService'));
let roleService = $injector.get(widgetContext.servicesMap.get('roleService'));
let currentUser = widgetContext.$scope.currentUser;
let currentOwnerInfo = getCurrentOwnerInfo();

console.log(entityService);

class PageLink {

    constructor(pageSize, page, textSearch, sortOrder) {
      this.textSearch = textSearch;
      this.pageSize = pageSize;
      this.page = page;
      this.sortOrder = sortOrder;
    }

    nextPageLink() {
      return new PageLink(this.pageSize, this.page + 1, this.textSearch, this.sortOrder);
    }

    toQuery() {
      let query = `?pageSize=${this.pageSize}&page=${this.page}`;
      if (this.textSearch && this.textSearch.length) {
        query += `&textSearch=${this.textSearch}`;
      }
      if (this.sortOrder) {
        query += `&sortProperty=${this.sortOrder.property}&sortOrder=${this.sortOrder.direction}`;
      }
      return query;
    }
}

// ************************************* LOGGED IN USER INFO *************************************

function getCurrentOwnerInfo(){
    let currentOwnerInfo = { "type": "CUSTOMER", "id": currentUser.customerId};
    if(currentUser.authority === "TENANT_ADMIN"){
        currentOwnerInfo = { "type": "TENANT", "id": currentUser.tenantId };
    }
    return currentOwnerInfo;
}

openAddEntityDialog();

function openAddEntityDialog() {
   customDialog.customDialog(htmlTemplate, AddEntityDialogController).subscribe();
}

function AddEntityDialogController(instance) {
   let vm = instance;

    vm.geocoded = false;
    vm.userGroupList = getEntityGroups("USER");
    vm.assetList = getGroupAllEntities("ASSET");
    vm.exploitantsAlertesEmailList = getExploitantsAlertesEmailList();

    vm.rolesId = { 'Role de groupe - Client final': {}, 'Role générique - Client final': {} };
    vm.roleGroupeClients = {};
    vm.roleGeneriqueClients = {};

    vm.googleGeocodedAddress = {'street_number': '', 'route': '', 'locality': '', 'postal_code': '', 'adresse': '', 'address': ''};

    vm.addEntityFormGroup = vm.fb.group({
        assetName: ['', [vm.validators.required, vm.validators.pattern(/^[a-zA-ZÀ-ÿ0-9-.,' ]*$/)]],
        assetAddress: ['', [vm.validators.required, vm.validators.pattern(/^[a-zA-ZÀ-ÿ0-9-.,' ]*$/)]],
        zones: [null, [vm.validators.pattern(/^([0-9]|[1-9][0-9]|100)$/)]],
        latitude: [null],
        longitude: [null]
    });

    vm.cancel = function() {
        vm.dialogRef.close(null);
    };

    getRolesClients();

    // ************************************************ RECUPERATION ROLES CLIENTS ************************************************

    function getRolesClients() {
        const roleGroupeClientsQuery = new PageLink(10, 0, "Role de groupe - Client final", null);
        const roleGeneriqueClientsQuery = new PageLink(10, 0, "Role générique - Client final", null);
        widgetContext.rxjs.forkJoin([
            roleService.getRoles(roleGroupeClientsQuery),
            roleService.getRoles(roleGeneriqueClientsQuery),
        ]).subscribe(results => {
            vm.roleGroupeClients = results[0].data[0];
            vm.roleGeneriqueClients = results[1].data[0];
        });
    }

    // ************************************* GET GROUP ALL ENTITIES *************************************

    function getGroupAllEntities(entityType) {
        let entityArray = [];
        entityGroupService.getEntityGroupAllByOwnerId(currentOwnerInfo.type, currentOwnerInfo.id, entityType)
            .subscribe((entityGroupAllInfo) => { 
                const t = new PageLink(10000, 0, null, null);
                entityGroupService.getEntityGroupEntities(entityGroupAllInfo.id.id, t)
                    .subscribe((entityList) => {
                        entityList.data.forEach(element => {
                            entityArray.push(element);
                        });
                    });
            });
        return entityArray;
    }

    // ************************************* GET ENTITYGROUPS *************************************

    function getEntityGroups(entityGroupType) {
        let entityGroups = [];
        entityGroupService.getEntityGroupsByOwnerId(currentOwnerInfo.type, currentOwnerInfo.id, entityGroupType)
            .subscribe((entityGroupList) => { 
                entityGroupList.forEach(entityGroup => {
                    if(entityGroup.groupAll === false){
                        entityGroups.push(entityGroup);
                    }
                }); 
            });
        return entityGroups;
    }

    // ************************************* GET EXPLOITANTS ALERTES EMAILS LIST *************************************

    function getExploitantsAlertesEmailList() {
        let emailList = [];
        entityGroupService.getEntityGroupsByOwnerId(currentOwnerInfo.type, currentOwnerInfo.id, "USER")
            .subscribe((userGroupList) => { 
                const groupeExploitantsAlertes = userGroupList.find(userGroup => userGroup.name === "Exploitants alertés");
                const t = new PageLink(10000, 0, null, null);
                entityGroupService.getEntityGroupEntities(groupeExploitantsAlertes.id.id, t)
                    .subscribe((exploitantsAlertesList) => {
                        exploitantsAlertesList.data.forEach(exploitantAlerte => {
                            emailList.push(exploitantAlerte.email);
                        });
                    });
            });
        return emailList;
    }

    // ************************************************ CHECK VALIDITE FORMULAIRE ************************************************

    function checkFormValidity() {
        let alerts = "Alerte - Le formulaire est invalide pour les raisons suivantes :";
        const newLine = "\r\n"; 
        let result = true;

        const addressInput = vm.addEntityFormGroup.get('assetAddress').value.trim().toLowerCase();
        const assetName = vm.addEntityFormGroup.get('assetName').value.trim().toLowerCase();

        const assetNameValidity = checkAssetNameValidity(assetName);
        const assetValidity = checkEntityExistence(vm.assetList, assetName);
        const addressValidity = checkAddressValidity(addressInput);

        if(assetNameValidity === false) {
            alerts += newLine;
            alerts += '- Le nom du chantier est vide';
            result = false;
        }
        if(assetValidity === false) {
            alerts += newLine;
            alerts += '- Un chantier du même nom existe déjà dans cette agence';
            result = false;
        }
        if(addressValidity === false){
            alerts += newLine;
            alerts += '- L\'adresse renseignée est invalide';
            result = false;
        }

        if (result === false){
            alert(alerts);
        }
        return result;
    }

    function checkEntityExistence(entityList, testedEntityName) {
        let result = false;
        const testedEntityCheck = entityList.find(element => element.name.trim() === testedEntityName);
        if(testedEntityCheck === undefined) {
            result = true;
        }
        return result;
    }

    function checkAddressValidity(addressInput) {
        let result = false;
        if(addressInput.length !== 0){
            result = true;
        }
        return result;
    }

    function checkAssetNameValidity(assetName) {
        let result = false;
        if(assetName.trim().length !== 0){
            result = true;
        }
        return result;
    }

    // ************************************************ CREATION GROUPE DE CHANTIERS ************************************************ 

    function createAssetGroup(){
        const assetGroup = {
            name: 'Chantiers ' + vm.addEntityFormGroup.get('assetName').value,
            type: 'ASSET'
        };
        return entityGroupService.saveEntityGroup(assetGroup);
    }

    // ************************************************ CREATION GROUPE DE CLIENTS ************************************************ 

    function createUserGroup(){
        const userGroup = {
            name: 'Clients ' + vm.addEntityFormGroup.get('assetName').value,
            type: 'USER'
        };
        return entityGroupService.saveEntityGroup(userGroup);
    }


    // ************************************************ CREATION DES ASSETS + ATTRIBUTS + RELATIONS ************************************************

    function createAsset() {
        const assetName = vm.addEntityFormGroup.get('assetName').value;
        const asset = { "name": assetName, "type": "Building" };
        return assetService.saveAsset(asset);
    }

    function createZones() {
        let tasks = [];
        const assetName = vm.addEntityFormGroup.get('assetName').value;
        const zonesNumber = vm.addEntityFormGroup.get('zones').value;
        for(let i=1; i <= zonesNumber; i++) {
            const zone = { "name": `Zone ${i} ~ ${assetName}`, "type": "Zone" };
            tasks.push(assetService.saveAsset(zone));
        }
        if (tasks.length > 0) {
            return widgetContext.rxjs.forkJoin(tasks);
        } else {
            return widgetContext.rxjs.of([]);
        }
    }

    function addAssetGroupAttributes(assetGroupInfo, userGroupInfo, assetInfo) {
        const assetGroupAttributesArray = [ { key: 'userGroupId', value: userGroupInfo.id.id }, { key: 'assetId', value: assetInfo.id.id } ];
        return attributeService.saveEntityAttributes(assetGroupInfo.id, 'SERVER_SCOPE', assetGroupAttributesArray);
    }

    function addUserGroupAttributes(userGroupInfo, assetGroupInfo, assetInfo) {
        const userGroupAttributesArray = [ { key: 'assetGroupId', value: assetGroupInfo.id.id }, { key: 'assetId', value: assetInfo.id.id } ];
        return attributeService.saveEntityAttributes(userGroupInfo.id, 'SERVER_SCOPE', userGroupAttributesArray);
    }

    function addUserGroupPermissions(userGroupInfo, assetGroupInfo) {
        const roleGroupeClientsId = vm.roleGroupeClients.id.id;
        const roleGeneriqueClientsId = vm.roleGeneriqueClients.id.id;
        const groupPermission = {
            "entityGroupId": { "entityType": "ENTITY_GROUP", "id": assetGroupInfo.id.id },
            "roleId": { "entityType": "ROLE", "id": roleGroupeClientsId },
            "userGroupId": { "entityType": "ENTITY_GROUP", "id": userGroupInfo.id.id },
            "role": { "id": { "entityType": "ROLE", "id": roleGroupeClientsId }, "type": "GROUP" }
        };
        const genericPermission = {
            "roleId": { "entityType": "ROLE", "id": roleGeneriqueClientsId },
            "userGroupId": { "entityType": "ENTITY_GROUP", "id": userGroupInfo.id.id },
            "role": { "id": { "entityType": "ROLE", "id": roleGeneriqueClientsId }, "type": "GENERIC" }
        };
        const tasks = [
            roleService.saveGroupPermission(groupPermission),
            roleService.saveGroupPermission(genericPermission)
        ];
        if(tasks.length > 0) {
            return widgetContext.rxjs.forkJoin(tasks);
        } else {
            return widgetContext.rxjs.of([]);
        }
    }

    function addAssetAttributes(assetInfo, assetGroupInfo, userGroupInfo) {
        const assetAttributesArray = [	
            {key: 'assetAddress', value: vm.addEntityFormGroup.get('assetAddress').value},	
            {key: 'adresse', value: vm.googleGeocodedAddress.adresse},	
            {key: 'ville', value: vm.googleGeocodedAddress.locality},	
            {key: 'code postal', value: vm.googleGeocodedAddress.postal_code},	
            {key: 'latitude', value: vm.addEntityFormGroup.get('latitude').value},
            {key: 'longitude', value: vm.addEntityFormGroup.get('longitude').value},
            {key: 'emailList', value: vm.exploitantsAlertesEmailList},	
            {key: 'assetGroupId', value: assetGroupInfo.id.id },	
            {key: 'userGroupId', value: userGroupInfo.id.id },	
            {key: 'nomContact1', value: ''},	
            {key: 'prenomContact1', value: ''},	
            {key: 'fonctionContact1', value: ''},	
            {key: 'emailContact1', value: ''},	
            {key: 'telephoneContact1', value: ''},	
            {key: 'nomContact2', value: ''},	
            {key: 'prenomContact2', value: ''},	
            {key: 'fonctionContact2', value: ''},	
            {key: 'emailContact2', value: ''},	
            {key: 'telephoneContact2', value: ''},	
            {key: 'nombreCaves', value: ''},	
            {key: 'nombreEtages', value: ''},	
            {key: 'commentaires', value: ''}	
        ];
        return attributeService.saveEntityAttributes(assetInfo.id, 'SERVER_SCOPE', assetAttributesArray);
    }

    function addZonesAttributes(zonesInfoList, assetGroupInfo, userGroupInfo, assetInfo ) {
        let tasks = [];
        const defaultPlan = "data:image/png;base64,"
        const zoneAttributesArray = [	
            {key: 'assetAddress', value: vm.addEntityFormGroup.get('assetAddress').value},	
            {key: 'latitude', value: vm.addEntityFormGroup.get('latitude').value},	
            {key: 'longitude', value: vm.addEntityFormGroup.get('longitude').value},	
            {key: 'emailList', value: vm.exploitantsAlertesEmailList},	
            {key: 'assetGroupId', value: assetGroupInfo.id.id},	
            {key: 'userGroupId', value: userGroupInfo.id.id },	
            {key: 'assetId', value: assetInfo.id.id},
            {key: 'imageURL', value: defaultPlan}
        ];
        zonesInfoList.forEach(zoneInfo => {
            tasks.push(attributeService.saveEntityAttributes(zoneInfo.id, 'SERVER_SCOPE', zoneAttributesArray));
        });
        if(tasks.length > 0) {
            return widgetContext.rxjs.forkJoin(tasks);
        } else {
            return widgetContext.rxjs.of([]);
        }
    }

    function addZonesRelations(zonesInfoList, assetInfo) {
        let tasks = [];
        zonesInfoList.forEach(zoneInfo => {
            const newRelation = { "from": {"entityType": "ASSET", "id": assetInfo.id.id}, "to": {"entityType": "ASSET", "id": zoneInfo.id.id}, "type": "Contains", "typeGroup": "COMMON" };
            tasks.push(entityRelationService.saveRelation(newRelation));
        });
        if(tasks.length > 0) {
            return widgetContext.rxjs.forkJoin(tasks);
        } else {
            return widgetContext.rxjs.of([]);
        }
    }

    function addAssetsToAssetGroup(assetInfo, zonesInfoList, assetGroupInfo) {
        let tasks = [entityGroupService.addEntityToEntityGroup(assetGroupInfo.id.id, assetInfo.id.id)];
        zonesInfoList.forEach(zoneInfo => {
            tasks.push(entityGroupService.addEntityToEntityGroup(assetGroupInfo.id.id, zoneInfo.id.id));
        });
        if(tasks.length > 0) {
            return widgetContext.rxjs.forkJoin(tasks);
        } else {
            return widgetContext.rxjs.of([]);
        }
    }

    // ************************************* GEOCODAGE *************************************
    async function getGeocodeResult() {
        let addressInput = vm.addEntityFormGroup.get('assetAddress').value;
        let lat = vm.addEntityFormGroup.get('latitude').value;
        let lng = vm.addEntityFormGroup.get('longitude').value;
        if (vm.geocoded === false || addressInput !== vm.googleGeocodedAddress.address) {      

            const geocodeResult = await geocode(addressInput);
            const address_components = geocodeResult.data.results[0].address_components;

            lat = geocodeResult.data.results[0].geometry.location.lat;
            lng = geocodeResult.data.results[0].geometry.location.lng;
            addressInput = geocodeResult.data.results[0].formatted_address;

            const street_number = findAddressComponent(address_components, "street_number");
            const route = findAddressComponent(address_components, "route");
            const locality = findAddressComponent(address_components, "locality");
            const postal_code = findAddressComponent(address_components, "postal_code");
            const adresse = formatAdresse(street_number, route, addressInput);


            vm.addEntityFormGroup.get('assetAddress').setValue(addressInput);
            vm.addEntityFormGroup.get('latitude').setValue(lat);
            vm.addEntityFormGroup.get('longitude').setValue(lng);

            vm.googleGeocodedAddress = {
                "address": addressInput,
                "adresse": adresse,
                "street_number": street_number,
                "route": route,
                "locality": locality,
                "postal_code": postal_code
            };

        }
        return {'assetAddress': addressInput, 'latitude': lat, 'longitude': lng};
    }

    function formatAdresse(street_number, route, formatted_address) {
        let result = (street_number + ' ' + route).trim();
        if (result === '') {
            result = formatted_address.split(',')[0];
        }
        return result;
    }

    function findAddressComponent(address_components, address_component_type) {
        let result = '';
        const address_component_research = address_components.find(element => element.types.includes(address_component_type));
        if(address_component_research !== undefined) {
            result = address_component_research.long_name;
        }
        return result;
    }

    function geocode(addressInput){
        const googleMapsApiURL = 'https://maps.googleapis.com/maps/api/geocode/json';
        const config = {
            params: { 
                address: addressInput,
                key: 'AIzaSyD9gOvqlTHxqfJy2RJ9p66AISVURTF1pfE'
            }
        };
        return new Promise(resolve => { resolve(axios.get(googleMapsApiURL, config).catch((e) => console.log(e))) });
    }

    vm.geocode = async function() {
        if(vm.addEntityFormGroup.get('assetAddress').value.trim().length > 0) {
            await getGeocodeResult();
            vm.geocoded = true;
        } else {
            alert('Veuillez renseigner une adresse valide.');
        } 
    }

    // ************************************* ACTUALISER ET FERMER *************************************

    function end(){
        vm.addEntityFormGroup.markAsPristine();
        widgetContext.updateAliases();
        vm.dialogRef.close(null);
        // setTimeout(function(){ location.reload(); }, 500);
    }


    // ************************************* TESTS *************************************

    vm.save = function() {
        vm.addEntityFormGroup.markAsPristine();

        const formValidity = checkFormValidity();
        if(formValidity === true){
            Promise.resolve(getGeocodeResult())
                .then(() => {
                    widgetContext.rxjs.forkJoin([
                        createAssetGroup(),
                        createUserGroup(),
                        createAsset(),
                        createZones()
                    ]).subscribe(
                        (results) => {
                            const assetGroupInfo = results[0];
                            const userGroupInfo = results[1];
                            const assetInfo = results[2];
                            const zonesInfoList = results[3];
                            widgetContext.rxjs.forkJoin([
                                addAssetGroupAttributes(assetGroupInfo, userGroupInfo, assetInfo),
                                addUserGroupAttributes(userGroupInfo, assetGroupInfo, assetInfo),
                                addUserGroupPermissions(userGroupInfo, assetGroupInfo),
                                addAssetAttributes(assetInfo, assetGroupInfo, userGroupInfo),
                                addZonesAttributes(zonesInfoList, assetGroupInfo, userGroupInfo, assetInfo),
                                addZonesRelations(zonesInfoList, assetInfo),
                                addAssetsToAssetGroup(assetInfo, zonesInfoList, assetGroupInfo)
                            ]).subscribe((results) => { end() });
                    });
                })
                .catch(e => {
                    console.log(e);
                    alert('Une erreur est survenue lors de l\'opération demandée. Veuillez réessayer.');
                    vm.addEntityFormGroup.markAsPristine();
                });
        }
    };
}
